## Implementation

### Provider Registry
```typescript
// packages/shared/src/git-providers/registry.ts

type ProviderName = 'github' | 'gitlab' | 'bitbucket'

const providers: Record<ProviderName, () => GitProvider> = {
  github: () => new GitHubProvider(config.github),
  gitlab: () => new GitLabProvider(config.gitlab),
  bitbucket: () => new BitbucketProvider(config.bitbucket),
}

export function getProvider(name: ProviderName): GitProvider {
  const factory = providers[name]
  if (\!factory) throw new Error(`Unknown provider: ${name}`)
  return factory()
}

